import React, { useState, useEffect } from 'react';
import { Router, navigate } from '@reach/router';
import {Route,Switch} from "react-router-dom"
import './App.css';

import Navigation from './components/Navigation';
import Login from './components/Login';
import Register from './components/Register';
import Protected from './components/Protected';
import Content from './components/Content';
import Home from './components/main/Home';
import AddItem from './components/main/Admin/AddItem';
import EditItem from './components/main/Admin/EditItem'
import Main from './components/main/Main';


export const UserContext = React.createContext([]);

function App() {
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(true);

  const logOutCallback = async () => {
    await fetch('http://localhost:4000/logout', {
      method: 'POST',
      credentials: 'include', // Needed to include the cookie
    });
    // Clear user from context
    setUser({});
    // Navigate back to startpage
    navigate('/');
  }

  // First thing, check if a refreshtoken exist
  useEffect(() => {
    async function checkRefreshToken() {
      const result = await (await fetch('http://localhost:4000/refresh_token', {
        method: 'POST',
        credentials: 'include', // Needed to include the cookie
        headers: {
          'Content-Type': 'application/json',
        }
      })).json();
        setUser({
          accesstoken: result.accesstoken,
        });
        setLoading(false);
    }
    checkRefreshToken();
  }, []);

  if (loading) return <div>Loading ...</div>

  return (
    <div>
    <UserContext.Provider value={[user, setUser]}>
      <div className="app">
        <Navigation logOutCallback={logOutCallback} />
        {/*<Router id="router">
          <Login path="login" />
          <Register path="register" />
          <Protected path="protected" />
          <Content path="/" />
          <Home path="/Homee"/>
          <AddItem path="/AddItem"/>
          <Route exact path='/EditItem/:id' component={EditItem}/>
           <EditItem path="EditItem/:id"/>


          <Main path="/Main"/>
          </Router>*/}


         <Switch>
   <Route exact path='/Homee' component={Home}/>
   <Route exact path='/login' component={Login}/>
   <Route exact path='/AddItem' component={AddItem}/>
   <Route exact path='/protected' component={Protected}/>
   <Route exact path='/register' component={Register}/>
   <Route exact path='/Main' component={Main}/>
   <Route exact path='/component' component={Content}/>

   <Route exact path='/EditItem/:id' component={EditItem}/>



   </Switch> 









      </div>
    </UserContext.Provider>


        </div>
  );
}

export default App;
