import React, { useState } from 'react'
import axios from 'axios'
import Nav from '../Nav';
import { useHistory, useParams} from "react-router-dom"
 
const Status = () => {
    let history = useHistory();
    const {id}=useParams();
    const [item, setItem] = useState({
      title: "",
      thumbnailUrl: "",
      price: "",  
      description:"",
      category:"",
      Received:"",
      Pending:"",
      Delivered:""
       
    });
  
    const { title,thumbnailUrl,price,description,category   } = item;
    const onInputChange = e => {
      setItem({...item, [e.target.name]: e.target.value });
    };
  
    const onSubmit = async e => {
      e.preventDefault();
      await axios.post("http://localhost:1800/corders", item);
      history.push("/Homee");
    };

    return (
      <div>
      <Nav/>
        <div className="new">
            <h1>ADD ITEM</h1>

            <form onSubmit={e=>onSubmit(e)}>

           <input type="checkbox" id="Receivied" name="Received" value="Received" onChange={e=> onInputChange(e)}/>
            <label htmlFor="Received">Received</label><br/>
             <input type="checkbox" id="Pending" name="Pending" value="Pending" onChange={e=> onInputChange(e)}/>
               <label htmlFor="Pending">Pending</label><br/>
             <input type="checkbox" id="Delivered" name="Delivered" value="Delivered" onChange={e=> onInputChange(e)}/>
             <label htmlFor="Delivered">Delivered</label>
             <br/>   
            <button> Add Item</button>
            
       
        </form>
        </div>
        </div>
    )
}

export default Status
