import React, { useEffect, useState } from 'react'
import axios from "axios";
import { Link } from 'react-router-dom';
  
const Main = () => {
    const [items, setItems] = useState([]);
 

   


    useEffect(() => {
        loadUsers();
    }, []);

    const loadUsers = async () => {
        const result = await axios.get(`http://localhost:9000/items`)
        setItems(result.data.reverse());
    }

    const filterItem= (categItem)=>{
        const updatedItems = items.filter((curElem)=>{
             
            return curElem.category===categItem;
        })
        
        setItems(updatedItems)
    
    }
    
   
    return (
        <div>

    <div className="about">
                        <h1 className="home_heading">USER SIDE</h1>
                        
                         
                        
                        <div className="filter_button">
                        <button onClick={()=> filterItem('male')}>Male</button>
                        <button  onClick={()=> filterItem('female')}>Female</button>
                        <button  onClick={()=>loadUsers()}>All</button>
                        </div>
                        </div>
    <div className="cards">
    {
                items.map((item, index) => (

                        <div key={item.id}>
                       <div className="card">
                        <img src={item.thumbnailUrl} alt="item"/> 
                        <h3>{item.title}</h3>
                        <h3 >Rs.{item.price}</h3>
                        
                        </div>
                        </div>
                            
                        
                     
                ))
            }
            </div>
            

 

 
        </div>
    )
}

export default Main
