import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Nav from '../Nav';
import { useHistory, useParams} from "react-router-dom"
 
const Status = () => {
    let history = useHistory();
    const {id}=useParams();
    const [item, setItem] = useState({
      title: "",
      thumbnailUrl: "",
      price: "",  
      description:"",
      category:"",
      Received:"",
      Pending:"",
      Delivered:""
       
    });
  
    const { title,thumbnailUrl,price,description,category,Received,Pending,Delivered} = item;
    const onInputChange = e => {
      setItem({...item, [e.target.name]: e.target.value });
    };
  
    useEffect(()=>{
        loadUser();
    }, []);

    const onSubmit = async e => {
      e.preventDefault();
      await axios.post("http://localhost:1800/corders", item);
     };

    const loadUser=async()=>{
        const result=await axios.get(`http://localhost:7000/items/${id}`)
     setItem(result.data)
      }

    return (
      <div>
      <Nav/>
        <div className="new">
            <h1>ADD ITEM</h1>

            <form onSubmit={e=>onSubmit(e)}>
            <div class="new_user">
            <p>{title}</p>

            <img src={thumbnailUrl}/>
            <p>{price}</p>
        

           <input type="checkbox" id="Receivied" name="Received" checked={category=="Received"} value="Received" onChange={e=> onInputChange(e)}/>
            <label htmlFor="Received">Received</label><br/>
             <input type="checkbox" id="Pending" name="Pending" checked={category=="Pending"} value="Pending" onChange={e=> onInputChange(e)}/>
               <label htmlFor="Pending">Pending</label><br/>
             <input type="checkbox" id="Delivered" name="Delivered" checked={category=="Delivered"} value="Delivered" onChange={e=> onInputChange(e)}/>
             <label htmlFor="Delivered">Delivered</label>

            <button> Add Item</button>
            
        </div>
        </form>
        </div>
        </div>
    )
}

export default Status
