import React, { useEffect, useState } from 'react'
import axios from "axios";
import { Link } from 'react-router-dom';
const Home = () => {

    const [items, setItems] = useState([]);


    useEffect(() => {
        loadUsers();
    }, []);

    const loadUsers = async () => {
        const result = await axios.get(`http://localhost:9000/items`)
        setItems(result.data.reverse());
    }

    const deleteUser = async id => {
        await axios.delete(`http://localhost:9000/items/${id}`)
        loadUsers()
    }

    return (
        <div>

            <div className="table">
                <h1 className="home_heading">ADMIN SIDE</h1>



                <table>
                    <thead>
                        <tr className="index">
                            <th>#</th>
                            <th>Name</th>
                            <th>Image</th>
                            <th>Price</th>
                            <th>Action</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            items.map((item, index) => (

                                <tr key={item.id}>
                                    <td>{index + 1}</td>
                                    <td>{item.title}</td>
                                    <td ><img src={item.thumbnailUrl} alt="item"/></td>
                                    <td >Rs.{item.price}</td>
                                    <td>

                                        <button> <Link className="link" to={`EditItem/${item.id}`}>Edit</Link></button>
                                        <button className="link" onClick={() => deleteUser(item.id)}>Delete</button>
                                    </td>
                                </tr>
                            ))
                        }
                    </tbody>

                </table>
            </div>

        </div>
    )
}

export default Home
