import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useHistory, useParams} from "react-router-dom";
import Nav from '../Nav';

const ViewItem = () => {
    const[counter,setCounter]=useState(1)
     
    const add=(i)=>{
        setCounter(i+1)
    }

    const sub=(i)=>{
        if(i>1){
        setCounter(i-1)}
    }


    let history = useHistory();
    const {id}=useParams();
    const [item, setItem] = useState({
        title: "",
        thumbnailUrl: "",
        price: "",
        description:"",
        category:"",
        id:"",
        qty:"",
       total:""
        
         
    });
  
    const { title,thumbnailUrl,price,description,category,qty} = item;
    let {total}=item
    const onInputChange = e => {
      setItem({...item, [e.target.name]: e.target.value });
    };
  
    useEffect(()=>{
        loadUser();
    }, []);
  
    const onSubmit = async e => {
        
        await axios.post("http://localhost:1500/orders", item);
        history.push("/");
      };
  
    const loadUser=async()=>{
        const result=await axios.get(`http://localhost:8000/items/${id}`)
     setItem(result.data)
      }

      
    return (
        <div className="view">
        <Nav/> 
        <form onSubmit={e=>onSubmit(e)}>
            <h1>Item Description</h1>
            <img src={thumbnailUrl}/>
            <div className="view_box">
            <p><b>Item Name:</b>  {title}</p>
            <p><b>Price:</b>{price}</p>
            <p><b>Category:</b> {category}</p>
            <p><b>About:</b> {description}</p>
           <div className="counter">
            <p><b>QTY:</b></p><button  type="button" onClick={()=>sub(counter)}>-</button>

             <input value={counter} name="count" onChange={e=> onInputChange(e)}/>
            <button type="button" onClick={()=>add(counter)}>+</button>
            <p><b>Total Cost:</b> <input value={total=price*counter} name="total" onChange={e=> onInputChange(e)}/>Rs.{total}</p>
            </div>
            
            </div>
            
           
            <button type="submit"> Confirm Item</button>

         </form>

        



             
        </div>
    )
}

export default ViewItem
