            <div className="table">
                <h1 className="home_heading">Order List</h1>



                <table>
                    <thead>
                        <tr className="index">
                            <th>#</th>
                            <th>Item Name</th>
                            <th>Image</th>
                            <th>Price</th>
                            <th>Qty</th>
