 import React, { useContext, useEffect, useState } from 'react'
import axios from 'axios'
import { useHistory, useParams} from "react-router-dom";
import Nav from '../Nav';
 
  
 const ViewItem = () => {

    
    const[counter,setCounter]=useState(1)
     
    const add=()=>{
        setCounter(counter+1)
    }

    const sub=()=>{
        if(counter>1){
        setCounter(counter-1)}
    }


    let history = useHistory();
    const {id}=useParams();
    const [item, setItem] = useState({
        title: "",
        thumbnailUrl: "",
        price: "",
        description:"",
        category:"",
        
        
         
    });
  
    const { title,thumbnailUrl,price,description,category} = item;
    
   const[info,setInfo]=updateState({
    qty:"",
    amount:"" ,
    id:""
   })

    const onInputChange = e => {
      setItem({...item, [e.target.name]: e.target.value });
    };
  
    useEffect(()=>{
        loadUser();
        loadStatus();
    }, []);
  
    const onSubmit = async e => {
        
        await axios.post("http://localhost:1500/orders", item);
        
        history.push("/");
      };
  
    const loadUser=async()=>{
        const result=await axios.get(`http://localhost:7000/items/${id}`)
     setItem(result.data)
      }

      const loadStatus=async()=>{
        const result=await axios.post(`http://localhost:1500/orders/${id}`)
     setItem(result.data)
      }

     

      var amount=price*counter; 
      
       
  
    return (
       <div>
        
       <div className="view_nav">     
        <Nav/>
        </div>
         
         <div className="view">
        <form onSubmit={e=>onSubmit(e)}>
            <h1>Item Description</h1>
            <img src={thumbnailUrl}/>
            <div className="view_box">
            <p><b>Item Name:</b>  {title}</p>
            <p><b>Price:</b>{price}</p>
            <p><b>Category:</b> {category}</p>
            <p><b>Description:</b> {description}</p>
           

           
           
           
             
            
           <div className="counter">
            <p><b>QTY:</b></p><button  type="button" onClick={()=>sub(counter)}>-</button>

           
           <input onChange={e=> onInputChange(e)} value={counter}/>
           
            <button type="button" onClick={()=>add(counter)}>+</button>

            <p><b>Total Cost:</b>  Rs. {total}</p>
             
            </div>
            
            </div>
            
           
            <button type="submit"> Confirm Item</button>

         </form>

        



             
        </div>
        </div>
    )
}

export default ViewItem
