import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useHistory, useParams} from "react-router-dom";
import Nav from '../Nav';

const ViewItem = () => {
    const[counter,setCounter]=useState(1)
    const counting=(i)=>{
        setCounter(i+1)
    }
    let history = useHistory();
    const {id}=useParams();
    const [item, setItem] = useState({
        title: "",
        thumbnailUrl: "",
        price: "",
        description:"",
        category:"",
        id:"",
        qty:""
    });
  
    const { title,thumbnailUrl,price,description,category,qty} = item;
    const onInputChange = e => {
      setItem({...item, [e.target.name]: e.target.value });
    };
  
    useEffect(()=>{
        loadUser();
    }, []);
  
    const onSubmit = async e => {
        
        await axios.post("http://localhost:2000/orders", item);
        history.push("/");
      };
  
    const loadUser=async()=>{
        const result=await axios.get(`http://localhost:8000/items/${id}`)
     setItem(result.data)
      }
    return (
        <div className="view">
        <Nav/> 
        <form onSubmit={e=>onSubmit(e)}>
            <h1>Item Description</h1>
            <img src={thumbnailUrl}/>
            <div className="view_box">
            <p><b>Item Name:</b>  {title}</p>
            <p><b>Price:</b>{price}</p>
            <p><b>Category:</b> {category}</p>
            <p><b>About:</b> {description}</p>
            
            

           

            </div>
            <div className="counter">
             <button>-</button>
             <input value={counter}/>
            <button onClick={counting(counter)}>+</button>
            </div> 
           
            <button type="submit"> Confirm Item</button>

         </form>
             
        </div>
    )
}

export default ViewItem
