import React, { useEffect, useState } from 'react'
import axios from "axios";
 import { Link } from 'react-router-dom';
 import { useHistory, useParams} from "react-router-dom";

import Nav from '../Nav';




const Basket = () => {

    let history = useHistory();
    const {id}=useParams();
    const [item, setItem] = useState({
        title: "",
        thumbnailUrl: "",
        price: "",
        description:"",
        category:""
    });
  
    const { title,thumbnailUrl,price,description,category} = item;
    const onInputChange = e => {
      setItem({...item, [e.target.name]: e.target.value });
    };
    
    const [items, setItems] = useState([]);
    const [counter, setCounter] = useState(0);
   
    const addbtn=()=>{
        setCounter(counter+1)
    }
    
    const subbtn=()=>{
        if(counter>0){
        setCounter(counter-1)}
    }


    useEffect(() => {
        loadUsers();
        
    }, []);

    const loadUsers = async () => {
        const result = await axios.get(`http://localhost:1500/orders`)
        setItems(result.data.reverse());
    }

    const confirmOrder = async () => {
       const info={title,thumbnailUrl,price,qty,status,category,description} 
        const result = await axios.put(`http://localhost:1500/orders${id}`,info)
        setItems(result.data.reverse());
    }

    
    
     
    const deleteUser = async id => {
        await axios.delete(`http://localhost:1500/orders/${id}`)
       // await axios.delete(`http://localhost:1800/corders/${id}`)
        loadUsers()
    }

     
     
     
    return (
        <div>
        <Nav/>
        <form onSubmit={e=>confirmOrder(e)}>
            <div className="table">
                <h1 className="home_heading">Order List</h1>



                <table>
                    <thead>
                        <tr className="index">
                            <th>#</th>
                            <th>Item Name</th>
                            <th>Image</th>
                            <th>Price</th>
                            <th>Qty</th>
                            <th>Total</th>
                            <th>Action</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            items.map((item, index) => (

                                <tr key={item.id}>
                                    <td>{index + 1}</td>
                                    <td>{item.title}</td>
                                    <td ><img src={item.thumbnailUrl} alt="item"/></td>
                                    <td >Rs.{item.price}</td>
                                    <button type="button"  onClick={() =>subbtn()}>-</button>
                                    <input value={item.qty=counter}/>
                                    <button type="button" onClick={() =>addbtn()}>+</button>
                                    <td>Rs. {item.qty*item.price}</td>
                                   
                                    
                                    <td>
                                   
                                    <button type="submit"/> 
                                        <button> <Link className="link" to={`Status/${item.id}`}>Status</Link></button>
                                        <button className="link" onClick={() => deleteUser(item.id)}>Delete</button>
                                    </td>
                                </tr>
                            ))
                           
                        }

                        
                    </tbody>

                </table>
            </div>
            </form>
        </div>
    )
}

export default Basket
