import React, { useEffect, useState } from 'react'
import axios from "axios";
import { Link } from 'react-router-dom';
import Nav from '../Nav';
const Basket = () => {

    const [items, setItems] = useState([]);
    const [stat, setStat] = useState([]);


    useEffect(() => {
        loadUsers();
        itemStatus();
    }, []);

    const loadUsers = async () => {
        const result = await axios.get(`http://localhost:1500/orders`)
        setItems(result.data.reverse());
    }
    
     
    const deleteUser = async id => {
        await axios.delete(`http://localhost:1500/orders/${id}`)
        loadUsers()
    }

    return (
        <div>
        <Nav/>

            <div className="table">
                <h1 className="home_heading">Order List</h1>



                <table>
                    <thead>
                        <tr className="index">
                            <th>#</th>
                            <th>Item Name</th>
                            <th>Image</th>
                            <th>Price</th>
                            <th>Action</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            items.map((item, index) => (

                                <tr key={item.id}>
                                    <td>{index + 1}</td>
                                    <td>{item.title}</td>
                                    <td ><img src={item.thumbnailUrl} alt="item"/></td>
                                    <td >Rs.{item.price}</td>
                                    
                                   
                                    
                                    <td>
                                   
                                        
                                        
                                        <button className="link" onClick={() => deleteUser(item.id)}>Delete</button>
                                    </td>
                                </tr>
                            ))
                           
                        }

                        
                    </tbody>

                </table>
            </div>

        </div>
    )
}

export default Basket
